query userQuestionProgress($userSlug: String!) {
  userProfileUserQuestionProgress(userSlug: $userSlug) {
    numAcceptedQuestions {
      difficulty
      count
      __typename
    }
    numFailedQuestions {
      difficulty
      count
      __typename
    }
    numUntouchedQuestions {
      difficulty
      count
      __typename
    }
    __typename
  }
}
